import React, { Component } from 'react';
import { TextField } from '@material-ui/core';
import Tooltip from '@material-ui/core/Tooltip';
import IconButton from '@material-ui/core/IconButton';
import reminder from '../assest/reminderalarm.svg';
import colorplate from '../assest/colorplate.svg';
import image from '../assest/image.svg'
import archive from '../assest/archive.svg'
import morevert from '../assest/more.svg'
import pin from '../assest/pin.svg'
import PersonAddOutlinedIcon from '@material-ui/icons/PersonAddOutlined';
import './Note.scss';

class Note extends Component {
  constructor(props) {
    super(props)
    this.handleMouseHover = this.handleMouseHover.bind(this);
    this.state = {
      isHovering: false,
    }
  }

  handleMouseHover() {
    this.setState(this.toggleHoverState);
  }

  toggleHoverState(state) {
    return {
      isHovering: !state.isHovering,
    };
  }

  render() {
    return (

      <div className={this.props.listGridValue === true ? "NoteContainer" : "gridContainer"}
      onMouseEnter={this.handleMouseHover}
        onMouseLeave={this.handleMouseHover} >

        <div style = {{width : '95%'}}>
        <div style={this.props.listGridValue === true ?{ width: '100%', display: 'flex', flexDirection: 'row'} : {width :'50%'}}>
          <TextField
            style={{ width: '100%', paddingTop: '10px', fontWeight: 'bold' }}
            InputProps={{
              disableUnderline: true
            }}
            // disabled = {true}
            value={this.props.title}
          />

         {this.state.isHovering && <div>
          <Tooltip title="Pin note">
            <IconButton><img src={pin} className="logo" alt="logo" /></IconButton>
          </Tooltip>
          </div>}
        </div>

        <TextField
          style={{ width: '95%' }}
          InputProps={{
            disableUnderline: true,
          }}
          multiline={true}
          value={this.props.note}
        />

     </div>

        {
          this.state.isHovering && <div style={{ width: '95%', position: 'relative', display: 'flex', backgroundColor: '', justifyContent: 'space-between', top: '10px', flexDirection: 'row' }}>
            <div>
              <Tooltip title="Remind me">
                <IconButton><img src={reminder} className="Logo" alt="logo" /></IconButton>
              </Tooltip>

              <Tooltip title="Collaborator">
                <PersonAddOutlinedIcon className="Logo" />
              </Tooltip>

              <Tooltip title="Change Color">
                <IconButton><img src={colorplate} className="Logo" alt="logo" /></IconButton>
              </Tooltip>

              <Tooltip title="Add Image">
                <IconButton><img src={image} className="Logo" alt="logo" /></IconButton>
              </Tooltip>

              <Tooltip title="Archive">
                <IconButton><img src={archive} className="Logo" alt="logo" /></IconButton>
              </Tooltip>

              <Tooltip title="More">
                <IconButton><img src={morevert} className="Logo" alt="logo" /></IconButton>
              </Tooltip>
            </div>

          </div>
        }
      </div>

    );
  }
}

export default Note;
